INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96294, 'Erlotinib', 'Elagolix', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of erlotinib, which is primarily metabolized by the isoenzyme.', NULL, 'Alternative treatment lacking CYP450 3A4-inducing activity is recommended in patients receiving erlotinib.', NULL, 'Alternative treatment lacking CYP450 3A4-inducing activity is recommended in patients receiving erlotinib. If alternative treatment is unavailable and a potent inducer (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, St. John''s wort) is to be used for more than a few days, an increase in the dosage of erlotinib should be considered as tolerated at 2-week intervals. Close clinical and laboratory monitoring for altered efficacy and safety, including renal and liver functions and serum electrolytes, is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96398/', '[1] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.[2] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[3] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[4] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[5] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[6]