INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96496, 'Elbasvir', 'Binimetinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib. The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors. Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96600/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Mektovi (binimetinib)." Array BioPharma Inc., Boulder, CO.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96497, 'Grazoprevir', 'Boceprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of grazoprevir, which is a substrate of the isoenzyme. In 8 study subjects, administration of a single 100 mg dose of grazoprevir with the potent CYP450 3A4 inhibitor ketoconazole (400 mg once daily) increased grazoprevir peak plasma concentration (Cmax), systemic exposure (AUC) and trough plasma concentration (Cmin) by 13%, 200% and 100%, respectively, compared to administration of grazoprevir alone. High plasma levels of grazoprevir may increase the risk of adverse effects such as alanine aminotransferase (ALT) elevations. Ketoconazole also increased the Cmax, AUC and Cmin of a single 50 mg dose of elbasvir by 29%, 80% and 89%, respectively.', NULL, 'Concomitant use of elbasvir-grazoprevir with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of elbasvir-grazoprevir with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96601/', '[1] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Company Inc, Whitehouse Station, NJ.[2] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Company Inc, Whitehouse Station, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Company Inc, Whitehouse Station, NJ.', 1767363023);
