    ) VALUES (1381602, 'Diltiazem', 'Nitroprusside', 'Minor', 'The dosage of nitroprusside required to achieve a given hypotensive response is reduced by an average of 50% when diltiazem is coadministered. The mechanism is unknown. Since the risk of side effects from nitroprusside may be significantly decreased with a lower dose, this interaction may be advantageous.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
