    ) VALUES (1381628, 'Diltiazem', 'Pemigatinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', NULL, 'Concomitant use of pemigatinib with potent or moderate CYP450 3A4 inhibitors should be avoided.', NULL, 'Concomitant use of pemigatinib with potent or moderate CYP450 3A4 inhibitors should be avoided. If coadministration is required, a reduction in the dosage of pemigatinib is recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
