        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1381645, 'Diltiazem', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
