    ) VALUES (1381685, 'Diltiazem', 'Rilpivirine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', NULL, 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', NULL, 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
