    ) VALUES (1381699, 'Diltiazem', 'Salsalate', 'Minor', 'Diltiazem may enhance the antiplatelet effect of salicylates. The mechanism is unknown. If occult bleeding occurs, consider discontinuing or adjusting the dosage of one or both agents.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
