    ) VALUES (1381734, 'Diltiazem', 'Tacrolimus', 'Moderate', 'In vitro data suggest that some calcium channel blockers inhibit the CYP450 3A4 hepatic metabolism and produce elevated blood levels of macrolide immunosuppressants.', NULL, 'Careful monitoring of macrolide immunosuppressant and possibly calcium channel blocker blood concentrations is recommended if these drugs must be used together.', NULL, 'Careful monitoring of macrolide immunosuppressant and possibly calcium channel blocker blood concentrations is recommended if these drugs must be used together. Dose reductions of the immunosuppressant may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
