        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1381751, 'Diltiazem', 'Theophylline', 'Moderate', 'Diltiazem may inhibit the CYP450 3A4 hepatic metabolism of theophylline. Significant increases in serum theophylline levels may result.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
