INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96501, 'Grazoprevir', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of elbasvir and grazoprevir, both of which are substrates of the isoenzyme. Efavirenz is generally considered a moderate inducer of CYP450 3A4. More potent inducers such as carbamazepine or phenytoin may interact to an even greater extent.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of elbasvir-grazoprevir with efavirenz or potent CYP450 3A4 inducers is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of elbasvir-grazoprevir with efavirenz or potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96605/', '[1] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Company Inc, Whitehouse Station, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Company Inc, Whitehouse Station, NJ.', 1767365206);
