INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96819, 'Eletriptan', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to lorlatinib-mediated induction of CYP450 3A4 metabolism.', NULL, 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.', NULL, 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy. Avoid concomitant use with lorlatinib where minimal decreases in concentration of the CYP450 3A4 substrate may lead to serious therapeutic failure. If coadministration is required, the CYP450 3A4 substrate dosage should be increased in accordance with approved product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96923/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96820, 'Eletriptan', 'Methylene blue', 'Major', 'The 5-HT1 receptor agonists used to treat migraine headaches exhibit modest 5-HT1A activity. Theoretically, MAOIs may potentiate the serotonergic activity of 5-HT1 receptor agonists and increase the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. The mechanism is an additive pharmacodynamic effect resulting from MAOI inhibition of serotonin metabolism. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'Concomitant use of 5-HT1 receptor agonists with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Concomitant use of 5-HT1 receptor agonists with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine) should generally be avoided. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with 5-HT1 receptor agonists. Otherwise, patients should be closely monitored for adverse effects related to excessive serotonergic activity.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96924/', '[1] Pettinger WA, Soyangco FG, Oa