INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96823, 'Eletriptan', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96927/', '[1] "Product Information. ORAVIG (miconazole)." Strativa Pharmaceuticals, a Division of Par Pharmaceuticals, Inc., Woodcliff Lake, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96824, 'Eletriptan', 'Mifepristone', 'Major', 'Coadministration with mifepristone may significantly increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by mifepristone.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use of CYP450 3A4 substrates with narrow therapeutic ranges and high first-pass effect is considered contraindicated.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use of CYP450 3A4 substrates with narrow therapeutic ranges and high first-pass effect is considered contraindicated. These drugs include lovastatin, simvastatin, cyclosporine, ergot derivatives, fentanyl, oral midazolam, triazolam, pimozide, quinidine, sildenafil, sirolimus, and tacrolimus. The contraindication does not apply when mifepristone is used on a limited basis to terminate a pregnancy, but caution and close monitoring are advisable. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96928/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] He K, Woolf TF, Hollenberg PF "Mechanism-based inactivation of cytochrome P-450-3A4 by mifepristone (RU486)." J Pharmacol Exp Ther 288 (1999): 791-7[4] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam are increased by concomitant ingestion of grapefruit juice." Clin Pharmacol Ther 58 (1995): 127-31[5] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[6] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[7] "Product Information. Valium (diazepam)." Roche Laboratories, Nutley, NJ.[8] "Product Information. Xanax (alprazolam)." Pharmacia and Upjohn, Kalamazoo, MI.[9] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[10] "Product Information. Halcion (triazolam)." Pharmacia and Upjohn, Kalamazoo, MI.[11] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[12] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767365206);
