INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96846, 'Eletriptan', 'Pitolisant', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96950/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96847, 'Eletriptan', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in increased risk of vasospastic reactions associated with the use of 5-HT1 receptor agonists, such as coronary artery vasospasm, peripheral vascular ischemia, and colonic ischemia.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96951/', '[1] "Product Information. Relpax (eletriptan)." Pfizer U.S. Pharmaceuticals, New York, NY.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96848, 'Eletriptan', 'Procarbazine', 'Major', 'The 5-HT1 receptor agonists used to treat migraine headaches exhibit modest 5-HT1A activity. Theoretically, MAOIs may potentiate the serotonergic activity of 5-HT1 receptor agonists and increase the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. The mechanism is an additive pharmacodynamic effect resulting from MAOI inhibition of serotonin metabolism. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'Concomitant use of 5-HT1 receptor agonists with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Concomitant use of 5-HT1 receptor agonists with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine) should generally be avoided. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with 5-HT1 receptor agonists. Otherwise, patients should be closely monitored for adverse effects related to excessive serotonergic activity.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96952/', '[1] Pettinger WA, Soyangco FG, Oate