INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96872, 'Eletriptan', 'Telotristat ethyl', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96976/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[3] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96873, 'Eletriptan', 'Temsirolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96977/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[7] "Product Information. Torisel (t