INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96882, 'Eletriptan', 'Voriconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in increased risk of vasospastic reactions associated with the use of 5-HT1 receptor agonists, such as coronary artery vasospasm, peripheral vascular ischemia, and colonic ischemia.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96986/', '[1] "Product Information. Relpax (eletriptan)." Pfizer U.S. Pharmaceuticals, New York, NY.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (96883, 'Eletriptan', 'Vortioxetine', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96987/', '[1] Paruchuri P, Godkar D, Anandacoomarswamy D, Sheth K, Niranjan S "Rare case of serotonin syndrome with therapeutic doses of paroxetine." Am J Ther 13 (2006): 550-552[2] Ciraulo DA, Shader RI "Fluoxetine drug-drug interactions: I. Antidepressants and antipsychotics." J Clin Psychopharmacol 10 (1990): 48-50[3] "Product Information. Brintellix (vortioxetine)." Takeda Pharmaceuticals America, Lincolnshire, IL.[4] George TP, Godleski LS "Possible serotonin syndrome with trazodone addition to fluoxetine." Biol Psychiatry 39 (1996): 384-5[5] Fugh-Berman A "Herb-drug interactions." Lancet 355 (2000): 134-8[6] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[7] "Product Information. D.H.E. 45 (dihydroergotamine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[8] Ruiz F "Fluoxetine and the serotonin syndrome." Ann Emerg Med 24 (1994): 983-5[9] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[10] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[11] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[12] Bergeron L, Boule M, Perreault S "Serotonin toxicity associated with concomitant use of linezolid." Ann Pharmacother 39 (2005): 956-61[13] Wigen CL, Goetz MB "Serotonin syndrome and linezolid." Clin Infect Dis 34 (2002): 1651-2[14] Metz A "Interaction between fluoxetine and buspirone." Can J Psychiatry 35 (1990): 722-3[15] Tahir N "Serotonin syndrome as a consequence of drug-resistant infections: an interaction between linezolid and citalopram." J Am Med Dir Assoc 5 (2004): 111-3[16] "Product Information. Zoloft (sertraline)." Roerig Division, New York, NY.[17] Lane R, Baldwin D "Selective serotonin reuptake inhibitor--induced serotonin syndrome: revi