INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381915, 'Carfilzomib', 'Norgestrel', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of hormonal contraception also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Hormonal contraception associated with a risk of thrombosis should generally be avoided during use of carfilzomib. Advise female and male patients of reproductive potential to use effective contraception or abstain from sexual activity during treatment with carfilzomib for at least 30 and 90 days, respectively, following completion of therapy. Advise the patient to contact their physician immediately if pregnancy does occur during these times.', 'Synergism', 'Hormonal contraception associated with a risk of thrombosis should generally be avoided during use of carfilzomib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381916, 'Carfilzomib', 'Ocrelizumab', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', 'DDInter', 1767317475, 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381917, 'Carfilzomib', 'Olaparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with olaparib.', 'DDInter', 1767317475, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents. Complete blood counts should be performed at baseline and monthly thereafter. Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.', 'Synergism', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381918, 'Carfilzomib', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1381919, 'Carfilzomib', 'Oprelvekin', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of other agents also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis. Thromboprophylaxis should be considered according to local treatment protocols. Close monitoring for thromboembolic events is recommended in patients with known risk factors (e.g., prior thrombosis, hyperlipidemia, hypertension, smoking) for thromboembolism. Patients should be advised to seek immediate medical attention if they develop symptoms of thromboembolism, such as shortness of breath, chest pain, hemoptysis, or arm or leg swelling or pain.', 'Synergism', 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis.', NULL, NULL, 'Major', 'pharmacodynamic');
