INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382167, 'Itraconazole', 'Cariprazine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The dosage of cariprazine should be reduced in the presence of a potent CYP450 3A4 inhibitor. When initiating a potent CYP450 3A4 inhibitor during stable treatment with cariprazine, the manufacturer recommends reducing the current dosage of cariprazine by one-half. For patients taking 4.5 mg daily, the dosage should be reduced to 1.5 mg or 3 mg daily. For patients taking 1.5 mg daily, the dosing interval should be adjusted to every other day. Conversely, if cariprazine therapy is initiated during treatment with a potent CYP450 3A4 inhibitor, the recommended starting dosage of cariprazine is 1.5 mg on day 1 and day 3, with no dose administered on day 2. From day 4 onward, the dosage should be 1.5 mg daily, then increased to a maximum dosage of 3 mg daily. Following withdrawal of the CYP450 3A4 inhibitor, the dosage of cariprazine may need to be increased.', 'Metabolism', 'The dosage of cariprazine should be reduced in the presence of a potent CYP450 3A4 inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382168, 'Ivacaftor', 'Cariprazine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme. The extent to which other, less potent inhibitors of CYP450 3A4 may interact with cariprazine and its metabolites is unknown.', 'DDInter', 1767317475, 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as extrapyramidal symptoms, cognitive and motor impairment, hyperglycemia, dyslipidemia, weight gain, orthostatic hypotension, leucopenia, neutropenia, seizures and dysphagia, and the dosage of cariprazine adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382169, 'Cariprazine', 'Ivosidenib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', 'DDInter', 1767317475, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
