INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382190, 'Cariprazine', 'Lisinopril', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382191, 'Cariprazine', 'Lithium carbonate', 'Moderate', 'Coadministration of lithium with neuroleptic agents, particularly haloperidol, has been associated with rare cases of an encephalopathic syndrome characterized by weakness, lethargy, fever, tremors, confusion, extrapyramidal symptoms, leukocytosis, and elevated liver enzymes and blood urea nitrogen. This syndrome may be similar to, or the same as, neuroleptic malignant syndrome. Other, more common central nervous system effects may also be increased, such as dizziness, drowsiness, confusion, difficulty concentrating, and impairment in thinking, judgment, and motor coordination.', 'DDInter', 1767317475, 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents. Dosage adjustments or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
