INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97079, 'Ivacaftor', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the oral bioavailability of ranolazine, which has been shown in vitro to be a substrate of P-gp. Ranolazine can prolong the QT interval in a dose-dependent manner, thus increased plasma levels may potentiate the risk of ventricular arrhythmias including ventricular fibrillation and torsade de pointes.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors. Pharmacologic response to ranolazine should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the ranolazine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97183/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97080, 'Ivacaftor', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally. Relugolix is a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours. Patients should be monitored more frequently for adverse effects. Alternatively, treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97184/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97081, 'Ivacaftor', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97185/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97082, 'Elexacaftor', 'Repaglinide', 'Moderate', 'Coadministration with elexacaftor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by elexacaftor and its metabolite. The clinical relevance is unknown.', NULL, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elexacaftor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97186/', '[1] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[4] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Symdeko 4-W