INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97191, 'Fexofenadine', 'Eliglustat', 'Moderate', 'Coadministration with eliglustat may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by eliglustat.', NULL, 'Caution is advised when eliglustat is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when eliglustat is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eliglustat is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97295/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767365206);
