INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97290, 'Eliglustat', 'Risperidone', 'Moderate', 'Coadministration with eliglustat may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6.', NULL, 'Caution is advised if eliglustat is used concomitantly with medications that undergo metabolism primarily by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if eliglustat is used concomitantly with medications that undergo metabolism primarily by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eliglustat is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97394/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97291, 'Eliglustat', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'The use of eliglustat with a potent CYP450 3A4 inhibitor is considered contraindicated in CYP450 2D6 poor metabolizers (PMs) and intermediate metabolizers (IMs).', NULL, 'The use of eliglustat with a potent CYP450 3A4 inhibitor is considered contraindicated in CYP450 2D6 poor metabolizers (PMs) and intermediate metabolizers (IMs). The contraindication is extended to extensive metabolizers (EMs) when a moderate or potent CYP450 2D6 inhibitor is given concomitantly with a moderate or potent CYP450 3A4 inhibitor. In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a potent CYP450 3A4 inhibitor. Potent CYP450 3A4 inhibitors include itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, ceritinib, idelalisib, mibefradil, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, dronedarone, duloxetine, flecainide, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propafenone, propoxyphene, quinidine, ranolazine, sertraline, and terbinafine. Some drugs such as cobicistat, delavirdine, mibefradil, and ritonavir are dual CYP450 3A4 and 2D6 inhibitors, and they should not be used with eliglustat in any patient regardless of their CYP450 2D6 metabolizer status.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97395/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97292, 'Eliglustat', 'Rivaroxaban', 'Moderate', 'Coadministration with eliglustat may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by eliglustat.', NULL, 'Caution is advised when eliglustat is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when eliglustat is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eliglustat is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97396/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97293, 'Eliglustat', 'Rolapitant', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs).', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs). In the absence of a concomitant CYP450 3A4 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to IMs and EMs treated with a potent or moderate CYP450 2D6 inhibitor. Poor metabolizers are not affected by CYP450 2D6 inhibition (since they already have minimal functional levels of the isoenzyme) and may also receive the reduced dosage of eliglustat, so long as they are not treated with a CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97397/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97294, 'Eliglustat', 'Romidepsin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of romidepsin, which is a substrate of the efflux transporter. Although there are no formal drug interaction studies, the interaction may increase the risk of hematologic toxicities such as anemia, leukopenia, and thrombocytopenia as well as electrocardiographic changes such as QT interval prolongation and T-wave and ST-segment changes.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors. Pharmacologic response to romidepsin should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the romidepsin dosage adjusted as necessary. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97398/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.', 1767365206);
