INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97346, 'Theophylline', 'Atracurium', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97450/', '[1] Doll DC, Rosenberg H "Antagonism of neuromuscular blockage by theophylline." Anesth Analg 58 (1979): 139-40[2] Azar I, Kumar D, Betcher AM "Resistance to pancuronium in an asthmatic patient treated with aminophylline and steroids." Can Anaesth Soc J 29 (1982): 280-2[3] Daller JA, Erstad B, Rosado L, Otto C, Putnam CW "Aminophylline antagonizes the neuromuscular blockade of pancuronium but not vecuronium." Crit Care Med 19 (1991): 983-5', 1767365206);
