INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97359, 'Esomeprazole', 'Theophylline', 'Moderate', 'Proton pump inhibitors may increase the rate of theophylline absorption from sustained-release formulations. Chronic use of proton pump inhibitors produce sustained hypochlorhydria, which may enhance peristalsis in the small intestine and antiperistalsis in the proximal colon where theophylline is absorbed.', NULL, 'In patients treated with proton pump inhibitors, the possibility of an enhanced pharmacologic response to sustained-release theophylline should be considered.', NULL, 'In patients treated with proton pump inhibitors, the possibility of an enhanced pharmacologic response to sustained-release theophylline should be considered. Patients should be closely monitored for development of toxicity, particularly those with theophylline levels in the upper range of normal. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97463/', '[1] Sommers DK, van Wyk M, Snyman JR, Moncrieff J "The effects of omeprazole-induced hypochlorhydria on absorption of theophylline from a sustained-release formulation." Eur J Clin Pharmacol 43 (1992): 141-3[2] Sato J, Nakata H, Owada E, Kikuta T, Umetsu M, Ito K "Influence of usual intake of dietary caffeine on single-dose kinetics of theophylline in healthy human subjects." Eur J Clin Pharmacol 44 (1993): 295-8[3] Jonkman JH, Sollie FA, Sauter R, Steinijans VW "The influence of caffeine on the steady-state pharmacokinetics of theophylline." Clin Pharmacol Ther 49 (1991): 248-55[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365206);
