INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97448, 'Theophylline', 'Oxytetracycline', 'Moderate', 'Coadministration of a tetracycline may decrease theophylline plasma clearance and increase theophylline levels. Data are available for minocycline, tetracycline, and doxycycline, but are conflicting in some cases. This interaction appears to be more problematic if a tetracycline is administered for more than five days. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended. Patients should be advised to notify their physician if they experience any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats. It may be necessary to reduce theophylline dosage.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97552/', '[1] McCormack JP, Reid SE, Lawson LM "Theophylline toxicity induced by tetracycline." Clin Pharm 9 (1990): 546-9[2] Gotz VP, Ryerson GG "Evaluation of tetracycline on theophylline disposition in patients with chronic obstructive airways disease." Drug Intell Clin Pharm 20 (1986): 694-6[3] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part I)." Clin Pharmacokinet 20 (1991): 66-80[4] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[5] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97449, 'Theophylline', 'Pancuronium', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97553/', '[1] Doll DC, Rosenberg H "Antagonism of neuromuscular blockage by theophylline." Anesth Analg 58 (1979): 139-40[2] Azar I, Kumar D, Betcher AM "Resistance to pancuronium in an asthmatic patient treated with aminophylline and steroids." Can Anaesth Soc J 29 (1982): 280-2[3] Daller JA, Erstad B, Rosado L, Otto C, Putnam CW "Aminophylline antagonizes the neuromuscular blockade of pancuronium but not vecuronium." Crit Care Med 19 (1991): 983-5', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97450, 'Theophylline', 'Pantoprazole', 'Moderate', 'Proton pump inhibitors may increase the rate of theophylline absorption from sustained-release formulations. Chronic use of proton pump inhibitors produce sustained hypochlorhydria, which may enhance peristalsis in the small intestine and antiperistalsis in the proximal colon where theophylline is absorbed.', NULL, 'In patients treated with proton pump inhibitors, the possibility of an enhanced pharmacologic response to sustained-release theophylline should be considered.', NULL, 'In patients treated with proton pump inhibitors, the possibility of an enhanced pharmacologic response to sustained-release theophylline should be considered. Patients should be closely monitored for development of toxicity, particularly those with theophylline levels in the upper range of normal. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97554/', '[1] Sommers DK, van Wyk M, Snyman JR, Moncrieff J "The effects of omeprazole-induced hypochlorhydria on absorption of theophylline from a sustained-release formulation." Eur J Clin Pharmacol 43 (1992): 141-3[2] Sato J, Nakata H, Owada E, Kikuta T, Umetsu M, Ito K "Influence of usual intake of dietary caffeine on single-dose kinetics of theophylline in healthy human subjects." Eur J Clin Pharmacol 44 (1993): 295-8[3] Jonkman JH, Sollie FA, Sauter R, Steinijans VW "The influence of caffeine on the steady-state pharmacokinetics of theophylline." Clin Pharmacol Ther 49 (1991): 248-55[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97451, 'Theophylline', 'Peginterferon alfa-2a', 'Moderate', 'The coadministration with interferons has been associated with decreases in clearance and increases in plasma concentration and elimination half-life of theophylline. The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes, particularly, CYP450 1A2.', NULL, 'Caution is advised if theophylline or other methylxanthines are administered with an interferon.', NULL, 'Caution is advised if theophylline or other methylxanthines are administered with an interferon. Pharmacologic response and serum theophylline levels should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their methylxanthine regimen. Patients should be advised to contact their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97555/', '[1] "Product Information. Intron A (interferon alfa-2b)." Schering Laboratories, Kenilworth, NJ.[2] Sakai H, Okamoto T, Kikkawa Y "Suppression of hepatic drug metabolism by the interferon inducer polyriboinosinic acid:polyribocitidylic acid." J Pharmacol Exp Ther 263 (1992): 381-6[3] Jonkman JH, Nicholson KG, Farrow PR, et al "Effects of alpha-interferon on theophylline pharmacokinetics and metabolism." Br J Clin Pharmacol 27 (1989): 795-802[4] Kramer P, Tsuru M, Cook CE, et al "Effect of influenza vaccine on warfari