INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97480, 'Theophylline', 'Rofecoxib', 'Moderate', 'The coadministration with rofecoxib may increase the plasma concentrations of theophylline. The proposed mechanism is rofecoxib inhibition of CYP450 1A2, the isoenzyme primarily responsible for the metabolic clearance of theophylline.', NULL, 'Caution is advised during coadministration of theophylline with rofecoxib.', NULL, 'Caution is advised during coadministration of theophylline with rofecoxib. Theophylline plasma levels should be closely monitored and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rofecoxib therapy in patients who are stabilized on their theophylline regimen. Patients should be advised to notify their physician if they experience signs of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, palpitations, and cardiac arrhythmia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97584/', '[1] "Product Information. Vioxx (rofecoxib)." Merck & Co, Inc, West Point, PA.', 1767365206);
