INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97495, 'Theophylline', 'Sotalol', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97599/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Mue S, Sasaki T, Shibahara S, et alT "Influence of metoprolol on hemodynamics and respiratory function in asthmatic patients." Int J Clin Pharmacol Biopharm 17 (1979): 346-50[3] Lombardi TP, Bertino JS, Goldberg A, Middleton E, Slaughter RL "The effects of a beta-2 selective adrenergic agonist and a beta- nonselective antagonist on theophylline clearance." J Clin Pharmacol 27 (1987): 523-9[4] Horvath JS, Woolcock AJ, Tiller DJ, Donnelly P, Armstrong J, Caterson R "A comparison of metoprolol and propranolol on blood pressure and respiratory function in patients with hypertension." Aust N Z J Med 8 (1978): 1-6[5] Conrad KA, Nyman DW "Effects of metoprolol and propranolol on theophylline elimination." Clin Pharmacol Ther 28 (1980): 463-7', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97496, 'Theophylline', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may decrease the serum concentrations of theophylline. The proposed mechanism is accelerated clearance of theophylline due to induction of hepatic CYP450 isoenzymes, namely 1A2, by constituents of St. John''s wort.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Pharmacologic response and serum theophylline levels should be monitored more closely whenever St. John''s wort is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience loss of asthma control or worsening of respiratory symptoms.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97600/', '[1] Nebel A, Schneider BJ, Baker RK, Kroll DJ "Potential metabolic interaction between St. John''s wort and theophylline." Ann Pharmacother 33 (1999): 502[2] Fugh-Berman A "Herb-drug interactions." Lancet 355 (2000): 134-8[3] Izzo AA, Ernst E "Interactions between herbal medicines and prescribed drugs: a systematic review." Drugs 61 (2001): 2163-75[4] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97497, 'Theophylline', 'Stiripentol', 'Moderate', 'The concomitant administration of stiripentol may increase plasma concentrations and toxic effects of theophylline and caffeine. The mechanism is inhibition of CYP450 1A2 metabolism by stiripentol.', NULL, 'The manufacturer recommends that concomitant use should be avoided.', NULL, 'The manufacturer recommends that concomitant use should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97601/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97498, 'Theophylline', 'Succinylcholine', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97602/', '[1] Doll DC, Rosenberg H "Antagonism of neuromuscular blockage by theophylline." Anesth Analg 58 (1979): 139-40[2] Azar I, Kumar D, Betcher AM "Resistance to pancuronium in an asthmatic patient treated with aminophylline and steroids." Can Anaesth Soc J 29 (1982): 280-2[3] Daller JA, Erstad B, Rosado L, Otto C, Putnam CW "Aminophylline antagonizes the neuromuscular blockade of pancuronium but not vecuronium." Crit Care Med 19 (1991): 983-5', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97499, 'Theophylline', 'Sucralfate', 'Minor', 'Limited clinical data suggest that sucralfate may inhibit the oral absorption of theophylline. The clinical significance of this interaction is unknown. Patients should be monitored for clinical and laboratory evidence of altered theophylline effect. Patients should be advised to take theophylline at least 2 hours before or 6 hours after sucralfate to minimize a potential interaction and to notify their physician if they experience a worsening of their respiratory symptoms.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97603/', '[1] "Product Information. Carafate (sucralfate)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Sato J, Nakata H, Owada E, Kikuta T, Umetsu M, Ito K "Influence of usual intake of dietary caffeine on single-dose kinetics of theophylline in healthy human subjects." Eur J Clin Pharmacol 44 (1993): 295-8[3] Jonkman JH, Sollie FA, Sauter R, Steinijans VW "The influence of caffeine on the steady-state pharmacokinetics of theophylline." Clin Pharmacol Ther 49 (1991): 248-55[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97500, 'Theophylline', 'Sulfinpyrazone', 'Minor', 'In one study, concomitant administration of sulfinpyrazone resulted in a 22% increase in total theophylline clearance. Changes noted in the elimination of theophylline included an increase in metabolism and a decrease in renal excretion. The clinical significance of this interaction is unknown. If sulfinpyrazone and theophylline are given together, clinical monitoring of patient response and serum theophylline levels is recommended. Patients should be advised to report worsening of their respiratory symptoms to their physician.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97604/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Birkett DJ, Miners JO, Attwood J "Evidence for a dual action of sulphinpyrazone on drug metabolism in man: theophylline-sulphinpyrazone interactions." Br J Clin Pharmacol 15 (1983): 567-9', 1767365206);
