INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97501, 'Theophylline', 'Tacrine', 'Moderate', 'Tacrine may increase plasma theophylline levels twofold and increase the elimination half-life of theophylline. The mechanism is probably related to the fact that both agents are metabolized by CYP450 1A2. Other hepatically metabolized methylxanthines may also be affected.', NULL, 'If tacrine and a theophylline must be used concomitantly, dosage reductions are recommended, as is close observation for evidence of altered clinical and laboratory effects.', NULL, 'If tacrine and a theophylline must be used concomitantly, dosage reductions are recommended, as is close observation for evidence of altered clinical and laboratory effects. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97605/', '[1] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.', 1767363023);
