INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97513, 'Ticlopidine', 'Theophylline', 'Moderate', 'In one study, theophylline clearance was reduced by 37% and theophylline half-life increased by 42% during concomitant ticlopidine therapy. The exact mechanism of this interaction is not known, but ticlopidine probably interferes with theophylline metabolism.', NULL, 'Theophylline levels and clinical response should be monitored when ticlopidine therapy is initiated or discontinued.', NULL, 'Theophylline levels and clinical response should be monitored when ticlopidine therapy is initiated or discontinued. Patients should be advised to report any signs of theophylline toxicity during concurrent therapy, including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97617/', '[1] Colli A, Buccino G, Cocciolo M, Parravicini R, Elli GM, Scaltrini G "Ticlopidine-theophylline interaction." Clin Pharmacol Ther 41 (1987): 358-62[2] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97514, 'Timolol', 'Theophylline', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97618/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Mue S, Sasaki T, Shibahara S, et alT "Influence of metoprolol on hemodynamics and respiratory function in asthmatic patients." Int J Clin Pharmacol Biopharm 17 (1979): 346-50[3] Lombardi TP, Bertino JS, Goldberg A, Middleton E, Slaughter RL "The effects of a beta-2 selective adrenergic agonist and a beta- nonselective antagonist on theophylline clearance." J Clin Pharmacol 27 (1987): 523-9[4] Horvath JS, Woolcock AJ, Tiller DJ, Donnelly P, Armstrong J, Caterson R "A comparison of metoprolol and propranolol on blood pressure and respiratory function in patients with hypertension." Aust N Z J Med 8 (1978): 1-6[5] Conrad KA, Nyman DW "Effects of metoprolol and propranolol on theophylline elimination." Clin Pharmacol Ther 28 (1980): 463-7', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97515, 'Tocainide', 'Theophylline', 'Minor', 'Tocainide causes a decrease in theophylline clearance. The result is an increase in serum theophylline concentrations and a mild increase in the serum half-life of theophylline. The mechanism of this interaction is not known. Tocainide may interact in a similar way with other xanthines. Management consists of monitoring clinical status during therapy and checking serum theophylline levels as necessary. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97619/', '[1] Loi CM, Wei X, Parker BM, Korrapati MR, Vestal RE "The effect of tocainide on theophylline metabolism." Br J Clin Pharmacol 35 (1993): 437-40[2] Loi C, Wei X, Parker B, Vestal R "Effect of tocainide on theophylline elimination." Clin Pharmacol Ther 51 (1992): 155', 1767363023);
