INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97543, 'Eluxadoline', 'Eltrombopag', 'Major', 'Coadministration with inhibitors of organic anion transporting polypeptide (OATP) 1B1 may significantly increase the plasma concentrations of eluxadoline. Based on available data, inhibition of OATP1B1-mediated hepatic uptake appears to reduce both the hepatic first-pass extraction and systemic biliary clearance of eluxadoline.', NULL, 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors.', NULL, 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors. Patients should be monitored for adverse effects such as sedation, nausea, vomiting, constipation, abdominal pain, liver enzyme elevations, and pancreatitis.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97647/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Davenport JM, Covington P, Bonifacio L, McIntyre G, Venitz J "Effect of uptake transporters OAT3 and OATPIBI and efflux transporter MRP2 on the pharmacokinetics of eluxadoline." J Clin Pharmacol 55 (2015): 534-42[3] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.[4] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[8] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, C