INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97593, 'Eltrombopag', 'Ospemifene', 'Moderate', 'Ospemifene and its major metabolite, 4-hydroxyospemifene, may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic cation transporter (OCT)1, uridine diphosphate glucuronosyltransferase (UGT) 1A3, and/or UGT1A9. The proposed mechanism, based on in vitro data, is decreased clearance due to ospemifene-mediated inhibition of OCT1, UGT1A3, and/or UGT1A9.', NULL, 'Until more information is available, caution is advised when ospemifene is used concomitantly with drugs that are substrates of OCT1, UGT1A3, and/or UGT1A9, particularly those with a narrow therapeutic range.', NULL, 'Until more information is available, caution is advised when ospemifene is used concomitantly with drugs that are substrates of OCT1, UGT1A3, and/or UGT1A9, particularly those with a narrow therapeutic range. Patients should be monitored for the development of adverse effects.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97697/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[3] "Product Information. Osphena (ospemifene)." Shionogi USA Inc, Florham Park, NJ.', 1767363023);
