INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97602, 'Eltrombopag', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97706/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97603, 'Eltrombopag', 'Repaglinide', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eltrombopag. The interaction has been demonstrated for rosuvastatin, a known substrate of both OATP 1B1 and BCRP.', NULL, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eltrombopag is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97707/', '[1] Allred AJ, Bowen CJ, Park JW, et al. "Eltrombopag increases plasma rosuvastatin exposure in healthy volunteers." Br J Clin Pharmacol 72 (2011): 321-9[2] Kalliokoski A, Niemi M "Impact of OATP transporters on pharmacokinetics." Br J Pharmacol 158 (2009): 693-705[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97604, 'Eltrombopag', 'Revefenacin', 'Moderate', 'Coadministration of revefenacin with inhibitors of organic anion transporting polypeptide (OATP) 1B1 and/or 1B3 may increase systemic exposure to its active metabolite, which has been reported to be a substrate of the hepatic uptake transporters.', NULL, 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', NULL, 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97708/', '[1] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[2] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[3] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.[4] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[5] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[6] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.[7] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97605, 'Eltrombopag', 'Ribociclib', 'Moderate', 'Coadministration with ribociclib may increase the plasma concentrations and pharmacologic effects of drugs that are substrates of breast cancer resistance protein (BCRP), organic cation transporter-2 (OCT-2), multidrug and toxin extrusion 1 (MATE1), and/or bile salt efflux pump (BSEP) transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to ribociclib-mediated inhibition of these transport proteins at clinically relevant concentrations.', NULL, 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ribociclib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97709/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97606, 'Eltrombopag', 'Rifampicin', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eltrombopag. The interaction has been demonstrated for rosuvastatin, a known substrate of both OATP 1B1 and BCRP.', NULL, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eltrombopag is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97710/', '[1] Allred AJ, Bowen CJ, Park JW, et al. "Eltrombopag increases plasma rosuvastatin exposure in healthy volunteers." Br J Clin Pharmacol 72 (2011): 321-9[2] Kalliokoski A, Niemi M "Impact of OATP transporters on pharmacokinetics." Br J Pharmacol 158 (2009): 693-705[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97607, 'Eltrombopag', 'Rimegepant', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of rimegepant. The proposed mechanism is decreased clearance due to inhibition of P-gp- and/or BCRP-mediated metabolism of rimegepant.', NULL, 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', NULL, 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97711/', '[1] "Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals, New Haven, CT.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[3] "Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals, New Haven, CT.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97608, 'Eltrombopag', 'Rosuvastatin', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of HMG-CoA reductase inhibitors (statins). The proposed mechanism is decreased clearance due to eltrombopag inhibition of the OATP 1B1-mediated hepatic uptake of statins and/or their metabolites (e.g., simvastatin acid), with fluvastatin the least likely to be significantly affected. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally.', NULL, 'Lower statin dosages should be considered during coadministration with eltrombopag.', NULL, 'Lower statin dosages should be considered during coadministration with eltrombopag. In clinical trials with eltrombopag, a dosage reduction of rosuvastatin by 50% was recommended. No specific adjustment recommendations were given for the others. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97712/', '[1] Allred AJ, Bowen CJ, Park JW, et al. "Eltrombopag increases plasma rosuvastatin exposure in healthy volunteers." Br J Clin Pharmacol 72 (2011): 321-9[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Kalliokoski A, Niemi M "Impact of OATP transporters on pharmacokinetics." Br J Pharmacol 158 (2009): 693-705[6] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.[7] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[8] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[9] Kantola T, Kivisto KT, Neuvonen PJ "Grapefruit juice greatly increases serum concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 63 (1998): 397-402[10] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[11] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[12] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[13] Thompson PD, Clarkson P, Karas RH "Statin-associated myopathy." JAMA 289 (2003): 1681-90[14] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.[15] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[16] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363023);
