INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97873, 'Entrectinib', 'Empagliflozin', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanisms, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of BCRP transport protein.', NULL, 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib.', NULL, 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97977/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97874, 'Enzalutamide', 'Empagliflozin', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97978/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.[3] Benoist G, van Oort I, et al "Drug-drug interaction potential in men treated with enzalutamide: Mind the gap." Br J Clin Pharmacol 0 (2017): epub[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363023);
