INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1382194, 'Cariprazine', 'Lomitapide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme. The extent to which other, less potent inhibitors of CYP450 3A4 may interact with cariprazine and its metabolites is unknown.', NULL, 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as extrapyramidal symptoms, cognitive and motor impairment, hyperglycemia, dyslipidemia, weight gain, orthostatic hypotension, leucopenia, neutropenia, seizures and dysphagia, and the dosage of cariprazine adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
