INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (98159, 'Isavuconazonium', 'Linagliptin', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), such as dabigatran. The proposed mechanism is isavuconazole inhibition of P-gp-mediated efflux of these drugs.', NULL, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever isavuconazonium sulfate is added to or withdrawn from therapy with these drugs. Patients receiving concomitant dabigatran should be monitored closely for the development of bleeding complications and the dosage of dabigatran adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98263/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Mekinist (trametinib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (98160, 'Linagliptin', 'Ivosidenib', 'Moderate', 'Coadministration with potent inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may significantly decrease the plasma concentrations of linagliptin, which is a substrate for both. The interaction may produce subtherapeutic and likely ineffective plasma concentrations of linagliptin.', NULL, 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. For patients requiring use of such drugs, an alternative to linagliptin is strongly recommended. Other known inducers of CYP450 3A4 include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, modafinil, nafcillin, nevirapine, and various other anticonvulsants, although the extent to which they interact with linagliptin is unknown. If concomitant use with these agents is necessary, glycemic control should be closely monitored.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98264/', '[1] "Product Information. Tradjenta (linagliptin)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharma