INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382827, 'Chlorpromazine', 'Spironolactone', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382828, 'Cholestyramine', 'Spironolactone', 'Moderate', 'Rare cases of severe hyperchloremic, hyperkalemic, metabolic acidosis have been associated with coadministration of spironolactone and cholestyramine. While spironolactone alone can cause metabolic acidosis, the addition of cholestyramine could provide an anion exchange of chloride for bicarbonate in the small bowel. The resulting loss of bicarbonate predisposes the patient to a hyperchloremic, nonanion gap, metabolic acidosis in the presence of renal failure.', 'DDInter', 1767317475, 'If these drugs must be used together, careful monitoring of electrolytes and metabolic status is strongly recommended, particularly in the presence of renal insufficiency.', 'Others', 'If these drugs must be used together, careful monitoring of electrolytes and metabolic status is strongly recommended, particularly in the presence of renal insufficiency.', NULL, NULL, 'Moderate', 'pharmacodynamic');
