INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382841, 'Dalteparin', 'Spironolactone', 'Moderate', 'The concomitant use of potassium-sparing diuretics and heparin or low molecular weight heparins may increase the risk of hyperkalemia. Both types of medications have individually been associated with increased potassium levels or hyperkalemia. Patients with renal impairment, diabetes, old age, and severe or worsening heart failure may be at a greater risk.', 'DDInter', 1767317475, 'Caution is advised if potassium-sparing diuretics are used with heparin or low molecular weight heparin. Serum potassium levels and renal function should be checked regularly.', 'Synergism', 'Caution is advised if potassium-sparing diuretics are used with heparin or low molecular weight heparin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382842, 'Danaparoid', 'Spironolactone', 'Moderate', 'The concomitant use of potassium-sparing diuretics and heparin or low molecular weight heparins may increase the risk of hyperkalemia. Both types of medications have individually been associated with increased potassium levels or hyperkalemia. Patients with renal impairment, diabetes, old age, and severe or worsening heart failure may be at a greater risk.', 'DDInter', 1767317475, 'Caution is advised if potassium-sparing diuretics are used with heparin or low molecular weight heparin. Serum potassium levels and renal function should be checked regularly.', 'Synergism', 'Caution is advised if potassium-sparing diuretics are used with heparin or low molecular weight heparin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382843, 'Dapagliflozin', 'Spironolactone', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, and dehydration can occur after initiating treatment with SGLT-2 inhibitors, and the risk may be increased with concomitant use of other agents that can lower blood pressure.', 'DDInter', 1767317475, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382844, 'Daunorubicin', 'Spironolactone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317475, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382845, 'Daunorubicin (liposomal)', 'Spironolactone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317475, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382846, 'Deflazacort', 'Spironolactone', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', 'DDInter', 1767317475, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Patients on prolonged (i.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382847, 'Demeclocycline', 'Spironolactone', 'Minor', 'The coadministration of diuretics and tetracyclines may result in decreased renal function manifested by increases in serum creatinine and blood urea nitrogen (BUN). Usually, no clinical intervention is necessary, unless decreases in renal function occur. If renal function deteriorates, discontinuation of one or both agents may be necessary.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382848, 'Desvenlafaxine', 'Spironolactone', 'Moderate', 'Coadministration with diuretics may potentiate the risk of hyponatremia associated with the use of selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs). Elderly patients and patients taking diuretics or who are otherwise volume-depleted may be at greater risk of developing hyponatremia with SSRIs and SNRIs. Antihypertensive agents such as diuretics may potentiate the orthostatic effect that is occasionally observed upon the initiation of SSRI or SNRI therapy. Syncope and orthostatic hypotension tend to occur within the first week of SNRI/SSRI therapy but can occur at any time during treatment, particularly after a dosage increase. The use of SSRIs or SNRIs may also cause sustained increases in blood pressure and heart rate, which may antagonize the therapeutic effects of antihypertensive medications.', 'DDInter', 1767317475, 'Caution is recommended if SSRIs or SNRIs are prescribed in combination with diuretics, particularly in the elderly. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hyponatremia such as nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, and unsteadiness (which may lead to falls). More severe and/or acute cases may include hallucination, syncope, seizure, coma, respiratory arrest, and death. Discontinuation of SSRI/SNRI therapy should be considered in patients who develop symptomatic hyponatremia, and appropriate medical intervention instituted as necessary. Patients should also have their blood pressure and pulse monitored before and during SSRI/SNRI therapy, especially during the first few weeks and following a dosage increase. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is recommended if SSRIs or SNRIs are prescribed in combination with diuretics, particularly in the elderly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382849, 'Dexmedetomidine', 'Spironolactone', 'Moderate', 'Dexmedetomidine has been associated with hypotensive and negative chronotropic effects. Coadministration with other agents causing these effects, especially vasodilators and alpha-2 agonists, may result in additive effects on blood pressure and heart rate.', 'DDInter', 1767317475, 'Caution and monitoring of the patient''s hemodynamic status are recommended during coadministration.', 'Synergism', 'Caution and monitoring of the patient''s hemodynamic status are recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382850, 'Diatrizoate', 'Spironolactone', 'Moderate', 'Forced diuresis during administration of radiocontrast agents may increase the risk of renal impairment in patients who are at high risk for contrast-induced nephropathy.', 'DDInter', 1767317475, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy. Otherwise, some experts recommend discontinuing diuretics 1 to 2 days before administration of contrast media, depending on the clinical feasibility of doing so. The smallest effective dose of a nonionic, hypo- or iso-osmolar contrast medium (e.g., iohexol, iodixanol, iopamidol) should be used, since the risk of nephropathy is increased with increasing contrast dose and/or osmolarity. Repeat procedures with contrast media, if necessary, should not occur until at least 72 hours after the previous contrast exposure and renal function has fully recovered. Although it is not necessary to measure the serum creatinine levels of every patient before contrast administration, measurements should generally be made in patients receiving contrast agent by intraarterial administration (which is associated with increased risk of nephropathy relative to intravenous administration) and patients with a history of kidney disease, proteinuria, kidney surgery, diabetes, hypertension, gout, or other risk factors for nephropathy. Creatinine measurements should be continued for 24 to 48 hours after administration of contrast medium. It is important that patients be adequately hydrated with either saline or sodium bicarbonate.', 'Synergism', 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382851, 'Diazepam', 'Spironolactone', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382852, 'Dicoumarol', 'Spironolactone', 'Minor', 'Spironolactone may cause diuresis and hemoconcentration of clotting factors. The effects of some anticoagulants may be decreased. Data are available for warfarin. The INR or PT should be monitored, and oral coagulant dosage should be increased as needed.', 'DDInter', 1767317475, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382853, 'Diflunisal', 'Spironolactone', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', 'DDInter', 1767317475, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382854, 'Digitoxin', 'Spironolactone', 'Minor', 'Spironolactone has been variously reported to increase or decrease the elimination half-life and serum concentrations of digitoxin The exact mechanism is not known, it may be related to induction of metabolism of digitoxin to digoxin and inhibition of renal digoxin excretion. The clinical significance is unknown. The patient should be monitored for signs or symptoms of congestive heart failure and digitalis toxicity. Patients should be advised to notify their physician if they experience increased shortness of breath, coughing, and wheezing, or nausea, anorexia, visual disturbances, and irregular heartbeats.', 'DDInter', 1767317475, '-', 'Metabolism, Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1382855, 'Digoxin', 'Spironolactone', 'Minor', 'Spironolactone may reduce the tubular secretion of digoxin. Plasma clearance of digoxin may be decreased, and plasma levels may increase. Also, limited data indicate that spironolactone may have a negative inotropic side effect. Finally, spironolactone may interfere with some digoxin radioimmunoassays, and digoxin concentrations may be overestimated. During coadministration, the patient should be monitored for signs and symptoms of digoxin toxicity, levels should be checked when necessary, and the clinician should keep in mind the possibility that digoxin levels may falsely appear to be elevated. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, or irregular heartbeats.', 'DDInter', 1767317475, '-', 'Antagonism, Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
