INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383047, 'Ticagrelor', 'Spironolactone', 'Moderate', 'Coadministration with potent P-glycoprotein inhibitors may theoretically increase the plasma concentrations of ticagrelor, which is a substrate of the efflux transporter. However, the interaction has not been studied.', 'DDInter', 1767317475, 'Until more data are available, caution is recommended if ticagrelor must be used with potent P-glycoprotein inhibitors. It may be advisable to monitor pharmacologic response to ticagrelor more closely following the addition and withdrawal of these agents. Discontinuation of the concomitant medication may be required if an interaction is suspected.', 'Excretion', 'Until more data are available, caution is recommended if ticagrelor must be used with potent P-glycoprotein inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
