INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383070, 'Vismodegib', 'Spironolactone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein may increase the plasma concentrations of vismodegib, which has been shown in vitro to be a substrate of the efflux transporter. The potential for increased pharmacologic effects of vismodegib, including adverse effects such as muscle spasm, alopecia, dysgeusia/ageusia, weight loss, fatigue, nausea, vomiting, diarrhea, decreased appetite, constipation and arthralgia, should be considered.', 'DDInter', 1767317475, 'Caution is advised if vismodegib is used in combination with P-glycoprotein inhibitors.', 'Others', 'Caution is advised if vismodegib is used in combination with P-glycoprotein inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
