INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99025, 'Encorafenib', 'Tolvaptan', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the oral bioavailability of tolvaptan, which has been shown in vitro to be a substrate of the efflux transporter.', NULL, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine.', NULL, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine. Serum sodium concentration and neurologic status should be closely monitored, and the tolvaptan dosage adjusted accordingly. Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death. Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99130/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[3] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99026, 'Encorafenib', 'Topotecan', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', NULL, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99131/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[4] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99027, 'Encorafenib', 'Toremifene', 'Major', 'Toremifene has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided. If treatment with other QT-prolonging drugs is required, interruption of toremifene therapy should be considered. Caution and clinical monitoring are recommended if concomitant use is unavoidable. In patients at increased risk, electrocardiograms (ECGs) are recommended at baseline and as clinically indicated. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99132/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[10] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99028, 'Encorafenib', 'Tositumomab', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Tositumomab and iodine I 131 tositumomab alone can cause severe or life-threatening thrombocytopenia.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously. Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99133/', '[1] "Product Information. BexxarTherapy (tositumomab)." GlaxoSmithKline Inc, Oakville, IA.[2] "Product Information. Bexxar I 131 Therapeutic (iodine I 131 tositumomab)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363023);
