INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99132, 'Grepafloxacin', 'Oxycodone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of oxycodone, which is primarily metabolized by the isoenzyme. Increased oxycodone concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Itraconazole, telithromycin and voriconazole are considered potent inhibitors of CYP450 3A4.', NULL, 'Caution may be advisable when oxycodone is prescribed with weak CYP450 3A4 inhibitors.', NULL, 'Caution may be advisable when oxycodone is prescribed with weak CYP450 3A4 inhibitors. The risk of overdose may be increased following the initiation of a CYP450 3A4 inhibitor in patients already receiving oxycodone. Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension. Following discontinuation of the CYP450 3A4 inhibitor, patients should be monitored for reduced efficacy of oxycodone or development of withdrawal symptoms due to reduced plasma oxycodone levels.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99237/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] Nieminen TH, Hagelberg NM, Saari TI, et al "Grapefruit juice enhances the exposure to oral oxycodone." Basic Clin Pharmacol Toxicol 107 (2010): 782-8', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99133, 'Griseofulvin', 'Oxycodone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, methadone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99238/', '[1] Liu S-J, Wang RI "Case report of barbiturate-induced enhancement of methadone metabolism and withdrawal syndrome." Am J Psychiatry 141 (1984): 1287-8[2] Tong TG, Pond SM, Kreek MJ, et al "Phenytoin-induced methadone withdrawal." Ann Intern Med 94 (1981): 349-51[3] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988): 623-9[4] "Product Information. Ionsys (fentanyl)." Ortho McNeil Pharmaceutical, Raritan, NJ.[5] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[6]