INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383534, 'Carvedilol', 'Propylthiouracil', 'Moderate', 'The clearance of some beta-blockers with high extraction ratios may be reduced when a euthyroid state is achieved after the addition of antithyroid agents. Sotalol, nadolol, carteolol, and atenolol, which are primarily eliminated by the kidney, do not appear to be affected by changes in thyroid function.', 'DDInter', 1767317475, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed. Beta blocker dose reductions may be required.', 'Others', 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383535, 'Carvedilol', 'Protriptyline', 'Moderate', 'Limited data suggest that labetalol can increase the plasma concentrations of tricyclic antidepressants. The proposed mechanism is competition for the same metabolic pathway. The manufacturers of labetalol indicate that concomitant use with tricyclic antidepressants may increase the incidence of tremors and hypotensive events. Carvedilol may also interact similarly.', 'DDInter', 1767317475, 'Close monitoring for clinical response and tolerance is recommended whenever labetalol is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as tremor, dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or hypotension. Dose adjustments may be necessary.', 'Metabolism', 'Close monitoring for clinical response and tolerance is recommended whenever labetalol is added to or discontinued from an antidepressant regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
