INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383641, 'Metolazone', 'Metformin', 'Moderate', 'Diuretic-induced renal impairment and dehydration may increase the risk of lactic acidosis in patients who are concomitantly taking metformin. In addition, thiazides and other diuretics may interfere with glucose control by causing hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring is recommended if diuretics are coadministered with antidiabetic agents. Patients should be advised to monitor their blood glucose and to promptly notify their doctor if they experience possible signs of lactic acidosis (such as malaise, myalgia, respiratory distress, hyperventilation, slow or irregular heartbeat, somnolence, abdominal upset) or loss of glycemic control. Dose adjustments of metformin may be required. Likewise, patients should be observed for hypoglycemia if diuretics are withdrawn from their therapeutic regimen.', 'Synergism', 'Close clinical monitoring is recommended if diuretics are coadministered with antidiabetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
