INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383706, 'Cefdinir', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', 'DDInter', 1767317475, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383707, 'Cefdinir', 'Magaldrate', 'Moderate', 'INTERVAL: Aluminum- or magnesium-containing antacids may interfere with the rate and extent of absorption of cefdinir. The mechanism has not been described but is most likely due to a chelation effect by the antacid or a change in intragastric pH.', 'DDInter', 1767317475, 'If an antacid must be administered during cefdinir therapy, separate the times of administration by at least two hours.', 'Absorption', 'If an antacid must be administered during cefdinir therapy, separate the times of administration by at least two hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
