INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383804, 'Cefixime', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', 'DDInter', 1767317475, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383805, 'Cefixime', 'Mycophenolate mofetil', 'Moderate', 'Theoretically, drugs that alter the gastrointestinal flora (e.g., antibiotics) may reduce the oral bioavailability of mycophenolic acid products. Some investigators believe that antimicrobials may interfere with the enterohepatic recirculation of mycophenolic acid by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating mycophenolic acid from its glucuronide metabolite following first-pass metabolism.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', 'Absorption', 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
