INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383836, 'Cefotaxime', 'Porfimer sodium', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', 'DDInter', 1767317475, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383837, 'Cefotaxime', 'Probenecid', 'Moderate', 'Coadministration with probenecid may increase and prolong the serum concentrations of some cephalosporins. The proposed mechanism is competitive inhibition of renal tubular secretion by probenecid.', 'DDInter', 1767317475, 'Although probenecid has been used therapeutically to enhance serum levels of various beta-lactam antibiotics, the potential for increased adverse effects should be considered when probenecid is added to existing cephalosporin therapy, particularly when the latter is given at high dosages or to patients who are elderly or have renal dysfunction. Adjustment of the cephalosporin dosage may be required in accordance with the individual product labeling. For example, cefotaxime dosage should generally not exceed 6 grams/day when administered with probenecid. Probenecid reportedly does not affect the elimination of ceftazidime or ceftriaxone.', 'Excretion', 'Although probenecid has been used therapeutically to enhance serum levels of various beta-lactam antibiotics, the potential for increased adverse effects should be considered when probenecid is added to existing cephalosporin therapy, particularly when the latter is given at high dosages or to patients who are elderly or have renal dysfunction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
