INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383894, 'Aluminum hydroxide', 'Cefpodoxime', 'Moderate', 'INTERVAL: Coadministration with antacids or agents with acid-neutralizing effects may reduce the oral bioavailability of cefpodoxime proxetil and cefuroxime axetil. The proposed mechanism is a pH-dependent reduction in drug dissolution and absorption.', 'DDInter', 1767317475, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and after administration of the antibiotic.', 'Absorption', 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil should avoid taking antacids or oral medications that contain antacids (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1383895, 'Amikacin (liposome)', 'Cefpodoxime', 'Moderate', 'Coadministration of aminoglycosides and cephalosporins may increase the risk of nephrotoxicity. An increased incidence of nephrotoxicity has been reported during concomitant use of aminoglycosides and some, mostly older cephalosporins (e.g., cefaloridine, cefamandole, cefazolin, cefotaxime, cefoxitin, ceftazidime, cefuroxime, cephalothin, ceftriaxone). The risk may be greatest in the elderly or patients with preexisting renal impairment, when large doses are used, and during prolonged treatment.', 'DDInter', 1767317475, 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination. Renal function should be monitored closely. The same precaution may be applicable when aminoglycosides are administered via irrigation, intrapleurally, intraperitoneally or orally, since aminoglycosides can be systemically absorbed via these routes; however, clinical data are lacking.', 'Synergism', 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
