INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100008, 'Levothyroxine', 'Labetalol', 'Minor', 'Thyroid hormone therapy may reverse decreased hepatic blood flow associated with hypothyroidism. Increased hepatic metabolism and decreased serum levels of some beta-blockers may result. Data are available for propranolol only. No special precautions are necessary. When hypothyroidism is converted to a euthyroid state, a decrease in beta-blocking effectiveness is possible.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100122/', '[1] Aro A, Antila M, Korhonen T, Sundquist H "Pharmacokinetics of propranolol and sotalol in hyperthyroidism." Eur J Clin Pharmacol 21 (1982): 373-7[2] Wells PG, Feely J, Wilkinson GR, Wood AJ "Effect of thyrotoxicosis on liver blood flow and propranolol disposition after long-term dosing." Clin Pharmacol Ther 33 (1983): 603-8[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100009, 'Levothyroxine', 'Lansoprazole', 'Moderate', 'Concurrent administration of proton pump inhibitors (PPI) may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The mechanism of interaction is suspected to be PPI induced hypochlorhydria leading to reduced levothyroxine absorption since gastric acidity is an essential requirement for levothyroxine absorption.', NULL, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors.', NULL, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors. Consider the alteration in gastric pH caused by the PPI. Some authorities recommend separating administration of PPI and levothyroxine by several hours, however there are no studies showing improved absorption when PPIs are administered separately from levothyroxine. If concomitant administration is necessary, consider monitoring TSH level and watching for clinical evidence of reduced levothyroxine effects. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100123/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[6] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100010, 'Levothyroxine', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: The coadministration of phosphate binders, such as sevelamer and lanthanum, with levothyroxine may increase levels of thyroid stimulating hormone (TSH). The mechanism appears to involve binding of levothyroxine to the phosphate binder resulting in delayed or lack of levothyroxine absorption.', NULL, 'Levothyroxine should be administered at least 4 hours apart from phosphate binders.', NULL, 'Levothyroxine should be administered at least 4 hours apart from phosphate binders. It is recommended that TSH levels be monitored more closely during concomitant use.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100124/', '[1] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.', 1767363023);
