INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100048, 'Propylthiouracil', 'Levothyroxine', 'Moderate', 'Antithyroid agents such as methimazole and propylthiouracil can affect the therapeutic response to thyroid hormone therapy. These agents decrease thyroid hormone secretion, which may result in hypothyroidism. Conversely, thyroid hormone therapy can antagonize the pharmacologic effects of antithyroid agents by supplying an exogenous source of the hormone.', NULL, 'Concomitant use of antithyroid agents and thyroid hormones should generally be avoided due to mutually negating effects.', NULL, 'Concomitant use of antithyroid agents and thyroid hormones should generally be avoided due to mutually negating effects.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100162/', '[1] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[4] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
