INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100139, 'Iron protein succinylate', 'Thyroid, porcine', 'Moderate', 'INTERVAL: Concurrent administration of iron-containing products may decrease the oral bioavailability and pharmacologic effects of levothyroxine. The exact mechanism of interaction is unknown, but in vitro data suggest it may involve nonspecific adsorption of levothyroxine to iron at acidic pH levels, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and iron-containing preparations by at least 2 to 4 hours.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and iron-containing preparations by at least 2 to 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100253/', '[1] Campbell NR, Hasinoff BB, Stalts H, Rao B, Wong NC "Ferrous sulfate reduces thyroxine efficacy in patients with hypothyroidism." Ann Intern Med 117 (1992): 1010-3[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[4] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
