INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100194, 'Thyroid, porcine', 'Sevelamer', 'Moderate', 'INTERVAL: The coadministration of phosphate binders, such as sevelamer and lanthanum, with levothyroxine may increase levels of thyroid stimulating hormone (TSH). The mechanism appears to involve binding of levothyroxine to the phosphate binder resulting in delayed or lack of levothyroxine absorption.', NULL, 'Levothyroxine should be administered at least 4 hours apart from phosphate binders.', NULL, 'Levothyroxine should be administered at least 4 hours apart from phosphate binders. It is recommended that TSH levels be monitored more closely during concomitant use.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100308/', '[1] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100195, 'Thyroid, porcine', 'Simethicone', 'Moderate', 'INTERVAL: Concurrent administration of simethicone and/or antacid preparations may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The exact mechanism of interaction is unknown but may involve nonspecific adsorption of levothyroxine to polyvalent cations, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract. Additionally, because gastric acidity is required for adequate levothyroxine absorption, agents that effect intragastric pH may reduce levothyroxine absorption.', NULL, 'It is recommended to separate the times of administration of levothyroxine and simethicone and antacids or other antacid-containing preparations (e.', NULL, 'It is recommended to separate the times of administration of levothyroxine and simethicone and antacids or other antacid-containing preparations (e.g., didanosine buffered tablets or pediatric oral solution) by at least 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100309/', '[1] Schneyer CR "Calcium carbonate and reduction of levothyroxine efficacy." JAMA 279 (1998): 750[2] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[3] Neafsey PJ "Levothyroxine and calcium interaction: timing is everything." Home Healthc Nurse 22 (2004): 338-9[4] Cerner Multum, Inc